from Bio.Seq import Seq
obj_seq=Seq("")
aa_chains=obj_seq.translate()
aa_chains_list=str(aa_chains).split("*")
print(aa_chains_list)
print(len(aa_chains_list))

lengths=[]
for aa in aa_chains_list:
    lengths.append(len(aa))

import matplotlib.pyplot as plt
fig,axs=plt.subplots(2)
axs[0].plot(lengths)
axs[1].hist(lengths)
plt.show()


